# https://leetcode.com/problems/similar-string-groups/



# use union find
# create an isSimilar function where if two words are similar (differ by exactly 2 elements)
# you'll want to iterate between each pair in the list. So it'll be O(n^2). You'll check if each pair isSimilar
# then union findi the similar ones
# at the end, make sure each i in the parent array is the deepest parent
# and now return the length of the set of the parent array (ie. length of uniques)



def find(i, parent):
    if i != parent[i]:
        parent[i] = find(parent[i], parent)
    return parent[i]

def union(pi, pj, parent, rank):
    if rank[pi] > rank[pj]:
        parent[pj] = pi
        rank[pi] += rank[pj]
    else: 
        parent[pi] = pj
        rank[pj] += rank[pi]        

def isSimilar(u, v):
    if u == v: 
        return True

    diff = 0
    for i in range(len(u)):
        if u[i] != v[i]:
            diff +=1
            if diff > 2: 
                return False

    print(u, v, diff)
    if diff == 2:
        return True

def numSimilarGroups(strs):
    strs_set = set(strs)
    idx_strs = {}
    parent = [i for i in range(len(strs))]
    rank = [1 for _ in range(len(strs))]

    for i in range(len(strs)):
        u = strs[i]
        for j in range(i+1, len(strs)):
            v = strs[j]
            if isSimilar(u, v):
                print(u, v, 'similar')
                pu = find(i, parent)
                pv = find(j, parent)
                if pu != pv: 
                    union(pu, pv, parent, rank)
        
    for i in range(len(parent)):
        parent[i] = find(i, parent)
    
    print(parent)
    return len(set(parent))

